	// This is a function to set accordion functionality 
	function set_accordion() {
		var accordion = document.getElementsByClassName("accordion");
		for (var i = 0; i < accordion.length; i++) {
			accordion[i].addEventListener("click", function () {
				/* Toggle between adding and removing the "active" class,
				to highlight the button that controls the panel */
				this.classList.toggle("active");

				/* Toggle between hiding and showing the active panel */
				var panel = this.nextElementSibling;
				if (panel.style.display === "block") {
					panel.style.display = "none";
				} else {
					panel.style.display = "block";
				}
			});
		}
	}

	// This is a function to check if an entry is empty
	function isEmpty(data) {
		if (data.length < 1) {
			return true;
		} else {
			return null;
		}
	}

	// This is a function to toggle between all vendors and the search results
	function maxfooteDbToggleAllVendors() {
		$(document).on('click', '#maxfoote-toggle-all-vendors', function (event) {
			var all_vendors = document.getElementsByClassName("maxfoote-all-vendors");

			for (var i = 0; i < all_vendors.length; i++) {
				if (all_vendors[i].style.display === "block") {
					all_vendors[i].style.display = "none";
				} else {
					all_vendors[i].style.display = "block";
				}
			};

		});
	}

	// This is a function to delete a Vendor entry in the database on the admin side.
	function maxfooteDbAdminDeleteEntry() {
		$(document).on('click', '.maxfoote-delete-vendor', function (event) {
			var answer = confirm("Are you sure you want to delete this vendor?");
			if (answer) {
				var id = $(this).attr('data-dbid');
				console.log(id);
				var data = {
					'action': 'maxfootedb_admin_delete_entry_fromdb_action',
					'security': maxfooteDbPhpVariables.adminnonce3,
					'ID': id
				};
				$.post(ajaxurl, data, function (response) {

					// $(this).parent().parent().parent().css({ "pointer-events": "none" });

					$('.maxfoote-spinner').animate({ 'opacity': '1' }, 500)
					$('.maxfoote-displayentries-response-div-actual-container').html("<p class='maxfoote-displayentries-response-formatted-message-success'>Success! You just deleted this Vendor from the Database!<p>");
					setTimeout(function () {
						document.location.reload(true);
					}, 4000);

				});
				event.preventDefault ? event.preventDefault() : event.returnValue = false;
			} else {
				return;
			}
		});
	}

	// Function to add search parameters to the address bar
