			var vendortrades = $('#maxfootedb-search-trades').val();
			var vendorcerts = $('#maxfootedb-search-certs').val();
			var currenturl = window.location.href;

			var s = currenturl.substring(0, currenturl.indexOf('&tab=vendors2'));

			s = '&city=' + vendorcity + '&state=' + vendorstate + '&zip=' + vendorzips + '&trade=' + vendortrades + '&certs=' + vendorcerts;

			console.log(currenturl + s);

			window.location = currenturl + s;

